// src/Register.js
import React, { useState } from "react";
import api from "./api";
import { useNavigate, Link } from "react-router-dom";

export default function Register() {
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [role, setRole] = useState("Operator");
  const [note, setNote] = useState("");
  const [loading, setLoading] = useState(false);
  const nav = useNavigate();

  const submit = async (e) => {
    e.preventDefault();
    setNote("");
    if (!username.trim()) { setNote("Username is required."); return; }
    if (password.length < 8) { setNote("Password must be 8+ characters."); return; }

    setLoading(true);
    try {
      await api.post("auth/register/", { username: username.trim(), email: email.trim(), password, role });
      setNote("Registration successful — please login.");
      setTimeout(() => nav("/"), 700);
    } catch (err) {
      console.error(err);
      const data = err?.response?.data;
      if (data && data.username) setNote(Array.isArray(data.username) ? data.username.join(" ") : String(data.username));
      else if (data && data.detail) setNote(String(data.detail));
      else setNote("Registration failed. Try a different username.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="auth-wrap">
      <div className="auth-card">
        <h1>Create account</h1>
        <p className="lead">Create a user with a role so you can access role-specific dashboards.</p>

        <form onSubmit={submit} noValidate>
          <label className="label">Username</label>
          <input className="input" value={username} onChange={e => setUsername(e.target.value)} placeholder="Choose username" required />

          <label className="label">Email</label>
          <input className="input" value={email} onChange={e => setEmail(e.target.value)} placeholder="you@example.com" />

          <label className="label">Password</label>
          <input className="input" type="password" value={password} onChange={e => setPassword(e.target.value)} placeholder="At least 8 characters" required />

          <label className="label">Role</label>
          <select className="input" value={role} onChange={e => setRole(e.target.value)}>
            <option>Operator</option>
            <option>Analyst</option>
            <option>Admin</option>
          </select>

          <div style={{ marginTop: 12 }}>
            <button className="btn btn-primary" type="submit" disabled={loading}>{loading ? "Creating…" : "Register"}</button>
          </div>
        </form>

        <div style={{ marginTop: 12, display: 'flex', justifyContent: 'space-between' }}>
          <div style={{ color: "var(--muted)" }}>Already have an account? <Link to="/" className="link">Login</Link></div>
        </div>

        {note && <div className={`note ${note.includes("failed") || note.includes("required") ? "error" : "ok"}`}>{note}</div>}
      </div>
    </div>
  );
}
