// vessel-frontend/src/api.js
import axios from "axios";

const api = axios.create({
  baseURL: "http://127.0.0.1:8000/api/",
});

// Robust detection: matches 'auth/token', 'auth/register', 'auth/token/refresh'
// Works whether axios sends "auth/register/" or "/api/auth/register/" or full URL.
function isAuthEndpoint(url) {
  if (!url) return false;
  const u = url.toString();
  return /(^\/?auth\/token($|\/))|(^\/?auth\/register($|\/))|(^\/?auth\/token\/refresh($|\/))|auth\/token|auth\/register|auth\/token\/refresh/.test(u);
}

api.interceptors.request.use((config) => {
  // If this request is to an auth endpoint, don't attach Authorization header.
  if (isAuthEndpoint(config.url)) {
    if (config.headers && config.headers.Authorization) delete config.headers.Authorization;
    return config;
  }

  const token = localStorage.getItem("access_token");
  if (token) {
    if (!config.headers) config.headers = {};
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
}, (error) => Promise.reject(error));

export default api;
