// vessel-frontend/src/Register.js
import React, { useState } from "react";
import api from "./api";
import { useNavigate, Link } from "react-router-dom";

export default function Register() {
  const [username, setU] = useState("");
  const [email, setEmail] = useState("");
  const [password, setP] = useState("");
  const [role, setRole] = useState("Operator");
  const [err, setErr] = useState("");
  const nav = useNavigate();

  const submit = async (e) => {
    e.preventDefault();
    setErr("");
    try {
      await api.post("auth/register/", { username, email, password, role });
      alert("Registration successful — now log in.");
      nav("/"); // go to login page
    } catch (e) {
      console.error("Register error:", e.response?.data || e);
      if (e.response?.status === 400 && e.response.data?.username) {
        setErr("Username already exists. Choose another.");
      } else {
        setErr("Registration failed. Check input or server.");
      }
    }
  };

  return (
    <div className="auth-container">
      <h2>Create Account</h2>
      <form onSubmit={submit}>
        <input className="auth-input" placeholder="Username" value={username} onChange={e=>setU(e.target.value)} required />
        <br/><br/>
        <input className="auth-input" placeholder="Email (optional)" value={email} onChange={e=>setEmail(e.target.value)} />
        <br/><br/>
        <input className="auth-input" type="password" placeholder="Password (min 6)" value={password} onChange={e=>setP(e.target.value)} required />
        <br/><br/>
        <select className="auth-input" value={role} onChange={(e)=>setRole(e.target.value)}>
          <option value="Operator">Operator</option>
          <option value="Analyst">Analyst</option>
          <option value="Admin">Admin</option>
        </select>
        <br/><br/>
        <button className="auth-btn" type="submit">Register</button>
      </form>
      {err && <p className="auth-error">{err}</p>}
      <div className="auth-link">Already have an account? <Link to="/">Login</Link></div>
    </div>
  );
}
