// paste as vessel-frontend/src/Login.js
import React, { useState } from "react";
import api from "./api";
import { useNavigate, Link } from "react-router-dom";

export default function Login() {
  const [username, setU] = useState("");
  const [password, setP] = useState("");
  const [err, setErr] = useState("");
  const nav = useNavigate();

  const submit = async (e) => {
    e.preventDefault();
    setErr("");
    try {
      const res = await api.post("auth/token/", { username, password });
      localStorage.setItem("access_token", res.data.access);
      localStorage.setItem("refresh_token", res.data.refresh);

      const profile = await api.get("auth/profile/");
      const role = profile.data.role || "Operator";
      if (role === "Operator") nav("/operator");
      else if (role === "Analyst") nav("/analyst");
      else nav("/admin");
    } catch (error) {
      console.error("[UI] login error:", error);
      setErr("Login failed — check username/password");
    }
  };

  return (
    <div style={{ padding: 20 }}>
      <h2>Login</h2>
      <form onSubmit={submit}>
        <input placeholder="username" value={username} onChange={(e)=>setU(e.target.value)} required />
        <br/><br/>
        <input type="password" placeholder="password" value={password} onChange={(e)=>setP(e.target.value)} required />
        <br/><br/>
        <button type="submit">Login</button>
      </form>

      {err && <p style={{ color: "red" }}>{err}</p>}

      <p>
        Don't have an account? <Link to="/register">Register here</Link>
      </p>
    </div>
  );
}
