// paste as vessel-frontend/src/Register.js
import React, { useState } from "react";
import api from "./api";
import { useNavigate } from "react-router-dom";

export default function Register() {
  const [username, setU] = useState("");
  const [email, setEmail] = useState("");
  const [password, setP] = useState("");
  const [role, setRole] = useState("Operator");
  const [err, setErr] = useState("");
  const [ok, setOk] = useState("");
  const nav = useNavigate();

  const submit = async (e) => {
    e.preventDefault();
    setErr(""); setOk("");
    try {
      // call register endpoint
      await api.post("auth/register/", { username, email, password, role });

      // after register, auto-login to get tokens
      const tokenRes = await api.post("auth/token/", { username, password });
      localStorage.setItem("access_token", tokenRes.data.access);
      localStorage.setItem("refresh_token", tokenRes.data.refresh);

      // fetch profile and redirect by role
      const profile = await api.get("auth/profile/");
      const r = profile.data.role || "Operator";
      if (r === "Operator") nav("/operator");
      else if (r === "Analyst") nav("/analyst");
      else nav("/admin");
    } catch (e) {
      console.error("Register error", e);
      setErr("Registration failed — check console or choose another username.");
    }
  };

  return (
    <div style={{ padding: 20, maxWidth: 420 }}>
      <h2>Register</h2>
      <form onSubmit={submit}>
        <input placeholder="username" value={username} onChange={(e)=>setU(e.target.value)} required />
        <br/><br/>
        <input placeholder="email (optional)" value={email} onChange={(e)=>setEmail(e.target.value)} />
        <br/><br/>
        <input placeholder="password" type="password" value={password} onChange={(e)=>setP(e.target.value)} required />
        <br/><br/>
        <label>
          Role:{" "}
          <select value={role} onChange={(e)=>setRole(e.target.value)}>
            <option value="Operator">Operator</option>
            <option value="Analyst">Analyst</option>
            <option value="Admin">Admin</option>
          </select>
        </label>
        <br/><br/>
        <button type="submit">Register & Login</button>
      </form>

      {err && <p style={{ color: "red" }}>{err}</p>}
      {ok && <p style={{ color: "green" }}>{ok}</p>}
    </div>
  );
}
