import axios from "axios";

const api = axios.create({
  baseURL: "http://127.0.0.1:8000/api/",
});

// Helper to detect auth endpoints where we MUST NOT send Authorization header
function isAuthEndpoint(url) {
  if (!url) return false;
  const u = url.toString();
  return u.includes("/auth/token") || u.includes("/auth/register") || u.includes("/auth/token/refresh");
}

api.interceptors.request.use((config) => {
  // If this is an "auth" endpoint (login/register/refresh), skip adding Authorization.
  if (isAuthEndpoint(config.url)) {
    // ensure no auth header is sent for auth endpoints
    if (config.headers) delete config.headers.Authorization;
    return config;
  }

  // For all other requests, attach access token (if present)
  const token = localStorage.getItem("access_token");
  if (token) {
    if (!config.headers) config.headers = {};
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
}, (error) => Promise.reject(error));

export default api;
