// src/Login.js
import React, { useState } from "react";
import api from "./api";
import { useNavigate, Link } from "react-router-dom";

export default function Login() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [note, setNote] = useState("");
  const [loading, setLoading] = useState(false);
  const nav = useNavigate();

  const submit = async (e) => {
    e.preventDefault();
    setNote("");
    setLoading(true);
    try {
      const res = await api.post("auth/token/", { username, password });
      localStorage.setItem("access_token", res.data.access);
      localStorage.setItem("refresh_token", res.data.refresh);
      const profile = await api.get("auth/profile/");
      const role = profile.data.role || "Operator";
      if (role === "Operator") nav("/operator");
      else if (role === "Analyst") nav("/analyst");
      else nav("/admin");
    } catch (err) {
      console.error(err);
      setNote("Invalid username or password.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="auth-wrap">
      <div className="auth-card" role="main" aria-labelledby="login-title">
        <h1 id="login-title">Maritime Platform</h1>
        <p className="lead">Log in to access dashboards and vessel analytics.</p>

        <form onSubmit={submit} noValidate>
          <label className="label">Username</label>
          <input className="input" value={username} onChange={e => setUsername(e.target.value)} placeholder="Enter username" required />

          <label className="label">Password</label>
          <input className="input" type="password" value={password} onChange={e => setPassword(e.target.value)} placeholder="Enter password" required />

          <div style={{ marginTop: 12 }}>
            <button className="btn btn-primary" type="submit" disabled={loading}>
              {loading ? "Signing in…" : "Login"}
            </button>
          </div>
        </form>

        <div style={{ marginTop: 14, display: "flex", justifyContent: "space-between", alignItems: "center" }}>
          <div style={{ fontSize: 13, color: "var(--muted)" }}>
            Don't have an account?
            <Link to="/register" className="link"> Register</Link>
          </div>
          <div style={{ fontSize: 13 }}>
            <Link to="#" className="link">Forgot?</Link>
          </div>
        </div>

        {note && <div className={`note ${note.includes("Invalid") ? "error" : "ok"}`}>{note}</div>}
      </div>
    </div>
  );
}
