// vessel-frontend/src/Login.js
import React, { useState } from "react";
import api from "./api";
import { useNavigate, Link } from "react-router-dom";

export default function Login() {
  const [username, setU] = useState("");
  const [password, setP] = useState("");
  const [err, setErr] = useState("");
  const nav = useNavigate();

  const submit = async (e) => {
    e.preventDefault();
    setErr("");
    try {
      const res = await api.post("auth/token/", { username, password });
      // store tokens
      localStorage.setItem("access_token", res.data.access);
      localStorage.setItem("refresh_token", res.data.refresh);

      // get profile and redirect by role
      const profile = await api.get("auth/profile/");
      const role = profile.data.role || "Operator";
      if (role === "Operator") nav("/operator");
      else if (role === "Analyst") nav("/analyst");
      else nav("/admin");
    } catch (error) {
      console.error("[UI] login error:", error.response?.data || error);
      setErr("Login failed — check username/password");
    }
  };

  return (
    <div className="auth-container">
      <h2>Maritime Login</h2>
      <form onSubmit={submit}>
        <input className="auth-input" placeholder="Username" value={username} onChange={(e)=>setU(e.target.value)} required />
        <br/><br/>
        <input className="auth-input" type="password" placeholder="Password" value={password} onChange={(e)=>setP(e.target.value)} required />
        <br/><br/>
        <button className="auth-btn" type="submit">Login</button>
      </form>
      {err && <p className="auth-error">{err}</p>}
      <div className="auth-link">Don't have an account? <Link to="/register">Register</Link></div>
    </div>
  );
}
